mapper.update:
update ideal
set ideal_name = #{idealName},
    win_rate = #{winRate},
    selection_id = #{selection.id},
    win_count = #{winCount}
where id =#{id}

mapper.select:
select id, ideal_name, win_rate, selection_id, win_count
from ideal
where id = #{id}

select id, ideal_name, win_rate, selection_id, win_count
from ideal
where selection_id = #{selectionId}
