mapper.update:
update ideal
ideal_name = #{ideal.idealName}, win_count = #{ideal.winCount}, battle_count = #{ideal.battleCount}, final_win_count = #{ideal.finalWinCount},
where id = #{id}

mapper.select:
select id, ideal_name, final_win_count, selection_id, win_count, battle_count
from ideal
where id = #{id}

select id, ideal_name, final_win_count, selection_id, win_count, battle_count
from ideal
where selection_id = #{selectionId}
