mapper.update:
update ideal
ideal_name = #{ideal.idealName}, win_rate = #{ideal.winRate}, win_count = #{ideal.winCount},
where id = #{id}

mapper.select:
select id, ideal_name, win_rate, selection_id, win_count
from ideal
where id = #{id}

select id, ideal_name, win_rate, selection_id, win_count
from ideal
where selection_id = #{selectionId}
