mapper.update:
update ideal_selection
set title =#{selection.title},
    body =#{selection.body},
    sub_count =#{selection.subCount},
    file_path =#{selection.filePath}
where id =#{id}

mapper.select:
select id, title, body, file_path, sub_count, creator_id
from ideal_selection
where id = #{id}

SELECT selection.id AS id, selection.title AS title, selection.body AS body, selection.file_path AS filePath, selection.sub_count AS subCount, selection.creator_id AS creatorId, selection.update_time AS updateTime, ideal.id AS idealList_id, ideal.ideal_name AS idealList_idealName, ideal.final_win_count AS idealList_finalWinCount, ideal.battle_count AS idealList_battleCount, ideal.win_count AS idealList_winCount, user.id AS creator_id, user.user_id AS creator_userId, user.password AS creator_password, user.user_name AS creator_userName FROM ideal_selection selection LEFT JOIN ideal ideal ON selection.id = ideal.selection_id LEFT JOIN user user ON selection.creator_id = user.id WHERE selection.id in ( SELECT id FROM ( SELECT id FROM ideal_selection
and title like concat('%', #{searchCond.title}, '%')
limit #{pageNum}, 100 ) AS creator_id );
